// src/pages/ProductPage.js

import React, { useState, useEffect } from 'react';
// import axios from 'axios'; // Uncomment when integrating with an API

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [storeFilter, setStoreFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [editProductId, setEditProductId] = useState(null);
  const [editProductData, setEditProductData] = useState({
    name: '',
    description: '',
    price: '',
    store: '',
  });

  useEffect(() => {
    // Replace this demo data with an axios API call if needed.
    const demoProducts = [
      { id: 1, store: 'Store A', name: 'Product 1', description: 'Description 1', price: 50 },
      { id: 2, store: 'Store B', name: 'Product 2', description: 'Description 2', price: 100 },
      { id: 3, store: 'Store A', name: 'Product 3', description: 'Description 3', price: 75 },
    ];
    setProducts(demoProducts);
  }, []);

  // Filter products by store and search term.
  const filteredProducts = products.filter((product) => {
    return (
      (storeFilter ? product.store === storeFilter : true) &&
      (searchTerm ? product.name.toLowerCase().includes(searchTerm.toLowerCase()) : true)
    );
  });

  // Delete functionality: remove product from state.
  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      setProducts((prev) => prev.filter((product) => product.id !== id));
    }
  };

  // Initiate edit mode: populate edit fields.
  const handleEditClick = (product) => {
    setEditProductId(product.id);
    setEditProductData(product);
  };

  // Handle changes to edit fields.
  const handleEditChange = (e) => {
    setEditProductData({ ...editProductData, [e.target.name]: e.target.value });
  };

  // Save edited product.
  const handleEditSave = () => {
    setProducts((prev) =>
      prev.map((product) =>
        product.id === editProductId ? { ...editProductData, id: editProductId } : product
      )
    );
    setEditProductId(null);
    setEditProductData({ name: '', description: '', price: '', store: '' });
  };

  // Cancel editing.
  const handleEditCancel = () => {
    setEditProductId(null);
    setEditProductData({ name: '', description: '', price: '', store: '' });
  };

  return (
    <div style={styles.container}>
      <h1>Product Management</h1>
      <div style={styles.filterContainer}>
        <label>
          Filter by Store:
          <select
            value={storeFilter}
            onChange={(e) => setStoreFilter(e.target.value)}
            style={styles.select}
          >
            <option value="">All Stores</option>
            <option value="Store A">Store A</option>
            <option value="Store B">Store B</option>
          </select>
        </label>
        <input
          type="text"
          placeholder="Search by product name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.searchInput}
        />
      </div>
      <div>
        {filteredProducts.map((product) => (
          <div key={product.id} style={styles.productCard}>
            {editProductId === product.id ? (
              <div style={styles.editContainer}>
                <input
                  type="text"
                  name="name"
                  value={editProductData.name}
                  onChange={handleEditChange}
                  placeholder="Name"
                  style={styles.inputField}
                />
                <input
                  type="text"
                  name="description"
                  value={editProductData.description}
                  onChange={handleEditChange}
                  placeholder="Description"
                  style={styles.inputField}
                />
                <input
                  type="number"
                  name="price"
                  value={editProductData.price}
                  onChange={handleEditChange}
                  placeholder="Price"
                  style={styles.inputField}
                />
                <input
                  type="text"
                  name="store"
                  value={editProductData.store}
                  onChange={handleEditChange}
                  placeholder="Store"
                  style={styles.inputField}
                />
                <div>
                  <button onClick={handleEditSave} style={styles.button}>
                    Save
                  </button>
                  <button onClick={handleEditCancel} style={styles.button}>
                    Cancel
                  </button>
                </div>
              </div>
            ) : (
              <div style={styles.productInfo}>
                <strong>{product.name}</strong> - {product.description} (${product.price}) -{' '}
                {product.store}
                <div>
                  <button onClick={() => handleEditClick(product)} style={styles.button}>
                    Edit
                  </button>
                  <button onClick={() => handleDelete(product.id)} style={styles.button}>
                    Delete
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

// Basic inline styles with media queries using JavaScript (for demonstration).
// For more advanced responsiveness, consider CSS media queries or a UI framework.
const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
  },
  filterContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    alignItems: 'center',
    marginBottom: '20px',
  },
  select: {
    marginLeft: '10px',
    padding: '5px',
  },
  searchInput: {
    marginLeft: '20px',
    padding: '5px',
    flex: '1 1 200px',
  },
  productCard: {
    border: '1px solid #ccc',
    padding: '10px',
    marginBottom: '10px',
    borderRadius: '4px',
    backgroundColor: '#fff',
  },
  productInfo: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  editContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  inputField: {
    padding: '5px',
    width: '100%',
    maxWidth: '300px',
  },
  button: {
    marginRight: '10px',
    padding: '5px 10px',
    cursor: 'pointer',
  },
};

export default ProductPage;
