import React, { useState, useEffect } from 'react';
// import axios from 'axios'; // Uncomment if using API calls

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [storeFilter, setStoreFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // For demo, we'll use static data. Replace with axios call if needed:
    // axios.get('http://localhost:5000/api/products').then(res => setProducts(res.data));
    const demoProducts = [
      { id: 1, store: 'Store A', name: 'Product 1', description: 'Description 1', price: 50 },
      { id: 2, store: 'Store B', name: 'Product 2', description: 'Description 2', price: 100 },
      { id: 3, store: 'Store A', name: 'Product 3', description: 'Description 3', price: 75 },
    ];
    setProducts(demoProducts);
  }, []);

  const filteredProducts = products.filter(product => {
    return (
      (storeFilter ? product.store === storeFilter : true) &&
      (searchTerm ? product.name.toLowerCase().includes(searchTerm.toLowerCase()) : true)
    );
  });

  return (
    <div>
      <h1>Product Management</h1>
      <div>
        <label>
          Filter by Store:
          <select value={storeFilter} onChange={e => setStoreFilter(e.target.value)}>
            <option value="">All Stores</option>
            <option value="Store A">Store A</option>
            <option value="Store B">Store B</option>
          </select>
        </label>
        <input
          type="text"
          placeholder="Search by product name..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          style={{ marginLeft: '10px' }}
        />
      </div>
      <ul>
        {filteredProducts.map(product => (
          <li key={product.id}>
            <strong>{product.name}</strong> - {product.description} (${product.price}) - {product.store}
            {/* Buttons for edit and delete can be added here */}
            <button style={{ marginLeft: '10px' }}>Edit</button>
            <button style={{ marginLeft: '5px' }}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductPage;
