import React, { useState, useEffect } from 'react';
// import axios from 'axios'; // Uncomment if using API calls

const InvoicePage = () => {
  const [invoices, setInvoices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // For demo, we'll use static data. Replace with axios call if needed:
    // axios.get('http://localhost:5000/api/invoices').then(res => { setInvoices(res.data); setLoading(false); });
    const demoData = [
      {
        id: 1,
        storeName: 'Store A',
        orderId: '1001',
        date: '2025-03-07',
        items: [
          { id: 1, product: 'Product 1', quantity: 2, regularPrice: 50, dealPrice: 45, tax: 5 },
          { id: 2, product: 'Product 2', quantity: 1, regularPrice: 100, dealPrice: 90, tax: 10 },
        ],
      },
      // Add more demo invoices if needed
    ];
    setInvoices(demoData);
    setLoading(false);
  }, []);

  const calculateTotals = (items) => {
    let totalWithoutTax = 0;
    let totalWithTax = 0;
    items.forEach(item => {
      const itemTotal = item.dealPrice * item.quantity;
      totalWithoutTax += itemTotal;
      totalWithTax += itemTotal + (item.tax * item.quantity);
    });
    return { totalWithoutTax, totalWithTax };
  };

  if (loading) return <div>Loading invoices...</div>;

  return (
    <div>
      <h1>Invoice Portal</h1>
      {invoices.map(invoice => {
        const { totalWithoutTax, totalWithTax } = calculateTotals(invoice.items);
        return (
          <div key={invoice.id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
            <h2>{invoice.storeName} - Order #{invoice.orderId}</h2>
            <p>Date: {invoice.date}</p>
            <table border="1" cellPadding="5" cellSpacing="0">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Quantity</th>
                  <th>Regular Price</th>
                  <th>Deal Price</th>
                  <th>Tax (per item)</th>
                  <th>Item Total</th>
                </tr>
              </thead>
              <tbody>
                {invoice.items.map(item => (
                  <tr key={item.id}>
                    <td>{item.product}</td>
                    <td>{item.quantity}</td>
                    <td>${item.regularPrice}</td>
                    <td>${item.dealPrice}</td>
                    <td>${item.tax}</td>
                    <td>${(item.dealPrice * item.quantity).toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <p>Grand Total (without tax): ${totalWithoutTax.toFixed(2)}</p>
            <p>Grand Total (with tax): ${totalWithTax.toFixed(2)}</p>
          </div>
        );
      })}
    </div>
  );
};

export default InvoicePage;
