// src/pages/InvoicePage.js
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ReactToPdf from 'react-to-pdf';

function InvoicePage() {
  const [invoices, setInvoices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [dateFilter, setDateFilter] = useState('');
  const [filteredInvoices, setFilteredInvoices] = useState([]);
  const pdfRef = useRef();

  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/invoices');
        setInvoices(response.data);
      } catch (error) {
        console.error('Error fetching invoices:', error);
      }
      setLoading(false);
    };
    fetchInvoices();
  }, []);

  useEffect(() => {
    if (dateFilter) {
      setFilteredInvoices(invoices.filter(inv => inv.date === dateFilter));
    } else {
      setFilteredInvoices(invoices);
    }
  }, [dateFilter, invoices]);

  const calculateTotals = (items) => {
    let totalWithoutTax = 0;
    let totalWithTax = 0;
    items.forEach(item => {
      const itemTotal = item.dealPrice * item.quantity;
      totalWithoutTax += itemTotal;
      totalWithTax += itemTotal + (item.tax * item.quantity);
    });
    return { totalWithoutTax, totalWithTax };
  };

  if (loading) return <div>Loading invoices...</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Invoice Portal</h1>
      <div style={{ marginBottom: '20px' }}>
        <label>
          Filter by Date:
          <input
            type="date"
            value={dateFilter}
            onChange={(e) => setDateFilter(e.target.value)}
            style={{ marginLeft: '10px' }}
          />
        </label>
      </div>
      <div ref={pdfRef}>
        {filteredInvoices.map(invoice => {
          const { totalWithoutTax, totalWithTax } = calculateTotals(invoice.items);
          return (
            <div
              key={invoice.id}
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginBottom: '10px',
                borderRadius: '4px',
                backgroundColor: '#fff'
              }}
            >
              <h2>{invoice.storeName} - Order #{invoice.orderId}</h2>
              <p>Date: {invoice.date}</p>
              <table border="1" cellPadding="5" cellSpacing="0" style={{ width: '100%' }}>
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Regular Price</th>
                    <th>Deal Price</th>
                    <th>Tax (per item)</th>
                    <th>Item Total</th>
                  </tr>
                </thead>
                <tbody>
                  {invoice.items.map(item => (
                    <tr key={item.id}>
                      <td>{item.product}</td>
                      <td>{item.quantity}</td>
                      <td>${item.regularPrice}</td>
                      <td>${item.dealPrice}</td>
                      <td>${item.tax}</td>
                      <td>${(item.dealPrice * item.quantity).toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <p>Grand Total (without tax): ${totalWithoutTax.toFixed(2)}</p>
              <p>Grand Total (with tax): ${totalWithTax.toFixed(2)}</p>
            </div>
          );
        })}
      </div>
      <ReactToPdf targetRef={pdfRef} filename="invoices.pdf" options={{ orientation: 'landscape' }}>
        {({ toPdf }) => (
          <button onClick={toPdf} style={{ marginTop: '20px', padding: '10px 20px' }}>
            Generate PDF
          </button>
        )}
      </ReactToPdf>
    </div>
  );
}

export default InvoicePage;
